interface StyleObject {
	[key: string]: string;
}

const parseStyle = (styleString: string) => {
	const styleObject: StyleObject = {};
	const styles = styleString.split(';');

	styles.forEach((style) => {
		const [propName, value] = style.split(':');
		if (propName && value) {
			const property = propName.trim().replace(/-([a-z])/g, (g) => g[1].toUpperCase());
			styleObject[property] = value.trim();
		}
	});

	return styleObject;
};

